%templates{idx} = imread( fullfile( inputFolder , templateFile ) ) ;%dimensions(idx).height = size( templates{idx},1) ;%dimensions(idx).width  = size( templates{idx},2) ;[templates, dimensions] = readInTemplates;img = rgb2gray(double(imread('thermometer.png')));[height, width] = size(img);%figure;imagesc(img);axis image;colormap gray;for( i = 1 : 30 )    temp = rgb2gray(double(templates{i}));    originalCorr = normxcorr2(temp, img);    offx = round(dimensions(i).width);    offy = round(dimensions(i).height);    newCorr = originalCorr(offy: offy + height - 1, offx: offx + width - 1);    corrArray(:, :, i) = newCorr;    end