ans = (size(candidates));len = ans(2);for (j = 1:len)    candidate = candidates(j);    x= candidate.col;    y = candidate.row;    templateIndex = maxIdx(y, x)        thisCorr = corrArray(: ,:, templateIndex);        window = thisCorr(y-1:y+1, x-1:x+1);    if(thisCorr(y, x) == max(window(:)))        drawAndLabelBox(x, y, templateIndex, dimensions);        drawnow;    endend        