function run(img, maxCorr, maxIdx, corrArray, dimensions, T)%display imagefigure;imagesc(img);colormap gray;%q3 eidx = 1;for (row = 1:1268)    for (col = 1:591)        if (maxCorr(row, col) > T)            candidates(idx).row = row;            candidates(idx).col = col;            idx = idx + 1;        end    endend%q3 fans = (size(candidates));len = ans(2);for (j = 1:len)    candidate = candidates(j);    x= candidate.col;    y = candidate.row;    templateIndex = maxIdx(y, x);        thisCorr = corrArray(: ,:, templateIndex);        window = thisCorr(y-1:y+1, x-1:x+1);    if(thisCorr(y, x) == max(window(:)))        drawAndLabelBox(x, y, templateIndex, dimensions);        drawnow;    endend